mapper.select:
select ini.id, ini.expert_id, ini.base_id, ini.`type`, ini.create_date, ini.enterprise_id, ini.del_flag, ini.status, pli.title, pli.post_name, pli.payment_method, pli.academic, pli.work_time, pli.work_hour, pli.work_address, pli.salary, exi.name as expertName, exi.images as expertImages, eni.name as enterName, eni.images as enterImages from interview_info ini left join pluralism_info pli on (pli.id = ini.base_id) left join expert_info exi on (exi.id = ini.expert_id) left join enterprise_info eni on (eni.id = ini.enterprise_id) where 1 = 1
AND pli.title LIKE CONCAT('%',#{interviewInfo.title},'%')
AND pli.post_name LIKE CONCAT('%',#{interviewInfo.postName},'%')
AND pli.payment_method = #{interviewInfo.paymentMethod}
AND ini.status = #{interviewInfo.status}
order by ini.create_date desc

select ini.id, ini.expert_id, ini.base_id, ini.`type`, ini.create_date, ini.enterprise_id, ini.del_flag, ini.status, pi.post_name, pi.academic, pi.work_time, pi.work_hour, pi.work_address, pi.salary, exi.name as expertName, exi.images as expertImages, eni.name as enterName, eni.images as enterImages from interview_info ini left join post_info pi on (pli.id = ini.base_id) left join expert_info exi on (exi.id = ini.expert_id) left join enterprise_info eni on (eni.id = ini.enterprise_id) where 1 = 1 AND ini.id IN
#{id}
order by ini.create_date desc

